import React, { useState, createContext, useContext } from 'react';
import Button from './button';
import { useBooking } from './bookingContext';
import formatTimeTo12Hour from '../utilities/formatTimeTo12Hour';

function BookingForm() {

    const {
      firstName, setFirstName,
      lastName, setLastName,
      date, setDate,
      time, setTime,
      guests, setGuests,
      occasion, setOccasion,
      seating, setSeating,
    } = useBooking();

  // form submission to proceed to confirmation step
  const handleFormSubmit = (e) => {
    e.preventDefault();
    setIsConfirming(true); // Set the state to true to show confirmation screen
  };

  // final confirmation submission
  const handleConfirm = () => {
    setFullyConfirmed(true);
    console.log("Reservation Confirmed:", { date, time, guests, occasion });
  };

  // cancel confirmation
  const handleCancel = () => {
    setIsConfirming(false); // Go back to the form view
  };

  return (
    <section className="section-container">
      <div className="reservation-container">
        <form onSubmit={handleFormSubmit} className="reservation-form">

          <div className="form-row-single">
            <label htmlFor="first-name">First Name</label>
            <input
              type="text"
              id="first-name"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              placeholder="First Name"
              required
            />
          </div>

          <div className="form-row-single">
            <label htmlFor="last-name">Last Name</label>
            <input
              type="text"
              id="last-name"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
              placeholder="Last Name"
              required
            />
          </div>

          <div className="form-row">
            <div className="form-row-item">
              <label htmlFor="date">Date</label>
              <input
                type="date"
                id="date"
                value={date}
                onChange={(e) => setDate(e.target.value)}
                required
              />
            </div>
            <div className="form-row-item">
              <label htmlFor="time">Time</label>
              <input
                type="time"
                id="time"
                value={time}
                onChange={(e) => setTime(e.target.value)}
                min="11:00"
                max="20:30"
                required
              />
            </div>
          </div>

          <div className="form-row-single">
            <label htmlFor="guests">How Many</label>
            <input
              type="number"
              id="guests"
              min="1"
              max="10"
              value={guests}
              onChange={(e) => setGuests(e.target.value)}
              required
            />
          </div>

          <div className="form-row-single">
            <label>Seating</label>
            <div className="form-row">
              <label>
                <input
                  type="radio"
                  name="seating"
                  value="inside"
                  checked={seating === "inside"}
                  onChange={(e) => setSeating(e.target.value)}
                />
                Inside
              </label>
              <label>
                <input
                  type="radio"
                  name="seating"
                  value="outside"
                  checked={seating === "outside"}
                  onChange={(e) => setSeating(e.target.value)}
                />
                Outside
              </label>
            </div>
          </div>

          <div className="form-row-single">
            <label htmlFor="occasion">Occasion</label>
            <select
              id="occasion"
              value={occasion}
              onChange={(e) => setOccasion(e.target.value)}
            >
              <option value="none">None</option>
              <option value="Birthday">Birthday</option>
              <option value="Anniversary">Anniversary</option>
            </select>
          </div>

          <div className="form-row">
            <Button type="submit" buttonClass="button-yellow" buttonText="Let's Do it!" />
          </div>
        </form>
      </div>
    </section>
  );
}

export default BookingForm;
