import React from 'react';
import BookingForm from './BookingForm';
import BookingFormConfirm from './BookingFormConfirm';
import BookingFormComplete from './BookingFormComplete';
import { BookingProvider, useBooking } from './bookingContext';

function Booking() {
  const { step, setStep } = useBooking();

  // Determine which component to render based on the current step
  let CurrentComponent;
  switch (step) {
    case "confirm":
      CurrentComponent = BookingFormConfirm;
      break;
    case "complete":
      CurrentComponent = BookingFormComplete;
      break;
    default:
      CurrentComponent = BookingForm;
  }

  return (
    <main className="vertical-stack" id="booking-container">
      <div>
        <CurrentComponent />
      </div>
      <button onClick={() => setStep("confirm")}>Next Step</button> {/* Example button to change step */}
    </main>
  );
}

export default function BookingWrapper() {
  return (
    <BookingProvider>
      <Booking />
    </BookingProvider>
  );
}
