import React, { useState } from 'react';
import Button from './button';
import formatTimeTo12Hour from '../utilities/formatTimeTo12Hour';

function BookingForm() {
  const {
    firstName: defaultFirstName,
    lastName: defaultLastName,
    date: defaultDate,
    time: defaultTime,
    guests: defaultGuests,
    seating: defaultSeating,
    occasion: defaultOccasion,
    setFirstName,
    setLastName,
    setDate,
    setTime,
    setGuests,
    setSeating,
    setOccasion,
    setStep
  } = useBooking();

  // Local state initialized with context values
  const [firstName, setLocalFirstName] = useState(defaultFirstName);
  const [lastName, setLocalLastName] = useState(defaultLastName);
  const [date, setLocalDate] = useState(defaultDate);
  const [time, setLocalTime] = useState(defaultTime);
  const [guests, setLocalGuests] = useState(defaultGuests);
  const [seating, setLocalSeating] = useState(defaultSeating);
  const [occasion, setLocalOccasion] = useState(defaultOccasion);

  const handleFormSubmit = (e) => {
    e.preventDefault();

    // Update context with local state values on submit
    setFirstName(firstName);
    setLastName(lastName);
    setDate(date);
    setTime(time);
    setGuests(guests);
    setSeating(seating);
    setOccasion(occasion);

    setStep("confirm"); // Move to confirmation step
  };

    return (
      <form onSubmit={handleFormSubmit}>
        <div className='form-row-single'>
          <label htmlFor="first-name">First Name</label>
          <input
            type="text"
            id="first-name"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
            placeholder="First Name"
            required
          />
        </div>
        <div className='form-row-single'>
          <label htmlFor="last-name">Last Name</label>
          <input
            type="text"
            id="last-name"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
            placeholder="Last Name"
            required
          />
        </div>
        <div className='form-row'>
          <div style={{ paddingRight: "30px" }}>
            <label htmlFor="date">Date</label>
            <input
              type="date"
              id="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="time">Time</label>
            <input
              type="time"
              id="time"
              value={time}
              onChange={(e) => setTime(e.target.value)}
              min="11:00"
              max="20:30"
              required
            />
          </div>
        </div>
        <div className='form-row-single'>
          <label htmlFor="guests">How Many</label>
          <input
            type="number"
            id="guests"
            min="1"
            max="10"
            value={guests}
            onChange={(e) => setGuests(e.target.value)}
            required
          />
        </div>
        <div className='form-row-single'>
          <label>Seating</label>
          <div className='form-row'>
            <label>
              <input
                type="radio"
                name="seating"
                className="input-radio"
                value="inside"
                checked={seating === "inside"}
                onChange={(e) => setSeating(e.target.value)}
              />
              Inside
            </label>
            <label>
              <input
                type="radio"
                name="seating"
                className="input-radio"
                value="outside"
                checked={seating === "outside"}
                onChange={(e) => setSeating(e.target.value)}
              />
              Outside
            </label>
          </div>
        </div>
        <div className='form-row-single'>
          <label htmlFor="occasion">Occasion</label>
          <select
            id="occasion"
            value={occasion}
            onChange={(e) => setOccasion(e.target.value)}
            style={{ width: "100%" }}
          >
            <option value="none">None</option>
            <option value="Birthday">Birthday</option>
            <option value="Anniversary">Anniversary</option>
          </select>
        </div>
        <div className='form-row'>
          <Button type="submit" buttonClass={"button-yellow"} buttonText="Let's Do it!" />
        </div>
      </form>
    );
  }

export default BookingForm;
