import React, { useState } from 'react';
import Button from './button';
import formatTimeTo12Hour from '../utilities/formatTimeTo12Hour';




function BookingForm() {

    // Gets today's date
    const today = new Date().toISOString().split('T')[0];
  
    // State variables to manage data
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [date, setDate] = useState(today);
    const [time, setTime] = useState('');
    const [guests, setGuests] = useState(1);
    const [occasion, setOccasion] = useState('none');
    const [seating, setSeating] = useState('inside');


    const [isConfirming, setIsConfirming] = useState(false);
    const [fullyConfirmed, setFullyConfirmed] =useState(false);
  // form submission to proceed to confirmation step
  const handleFormSubmit = (e) => {
    e.preventDefault();
    setIsConfirming(true); // Set the state to true to show confirmation screen
  };

  // final confirmation submission
  const handleConfirm = () => {
    setFullyConfirmed(true);
    console.log("Reservation Confirmed:", { date, time, guests, occasion });
  };

  // cancel confirmation
  const handleCancel = () => {
    setIsConfirming(false); // Go back to the form view
  };

  return (
    <section className={"section-container"} style={{color: "white", backgroundColor: "#495E57"}}>
        <div className={"width-limiter" }>
          <div className="reservation-container">
          {fullyConfirmed ? (
            <div>
              <h1>You're set to Go!</h1>
              <Button buttonText={'Return To Homepage'} buttonClass={'button-yellow'} link={"/"} />
            </div>
          ) : (!isConfirming ? (
        <form
          onSubmit={handleFormSubmit}
          className="reservation-form">

          <div className='form-row'>
          <input
            type="text"
            id="first-name"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
            placeholder="First Name"
            required
          />
          </div>

          <div className='form-row'>
          <input
            type="text"
            id="last-name"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
            placeholder="Last Name"
            required
          />
          </div>

        <div className='form-row'>
            <input
              type="date"
              id="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />

          <label htmlFor="time">Time:</label>
          <input
            type="time"
            id="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            min="11:00"
            max="20:30"
            required
          />
        </div>

        <div className='form-row'>
          <label htmlFor="guests">Number of Guests:</label>
          <input
            type="number"
            id="guests"
            min="1"
            max="10"
            value={guests}
            onChange={(e) => setGuests(e.target.value)}
            required
          />

          <label htmlFor="occasion">Occasion:</label>
            <select
              id="occasion"
              value={occasion}
              onChange={(e) => setOccasion(e.target.value)}
            >
              <option value="none">None</option>
              <option value="Birthday">Birthday</option>
              <option value="Anniversary">Anniversary</option>
            </select>
        </div>
        <div className='form-row'>
            <label>Seating Preference:</label>


            <label>
              <input
                type="radio"
                name="seating"
                value="inside"
                checked={seating === "inside"}
                onChange={(e) => setSeating(e.target.value)}
              />
              Inside
            </label>
            <label>
              <input
                type="radio"
                name="seating"
                value="outside"
                checked={seating === "outside"}
                onChange={(e) => setSeating(e.target.value)}
              />
              Outside
            </label>
          </div>

        <div className='form-row'>
          <Button type="submit" buttonClass={"button-yellow"} buttonText="Submit Reservation" />
        </div>
        </form>
      ) : (
        <div className="confirmation-section">
          <h2>Confirm Your Reservation</h2>
          <p></p>
          <p>Date: {date}</p>
          {time && <p>Time: {formatTimeTo12Hour(time)}</p>}
          <p>Number of Guests: {guests}</p>

           {/* makes sure to only display anything if occasion is NOT 'none'
          The expression after && will only run if the condition before it is true. */}
          {occasion !== "none" && <p>Occasion: {occasion}</p>}

          <Button buttonText="Confirm Reservation" buttonClass={"button-yellow"} onClick={handleConfirm} />
          <Button buttonText="Edit Reservation" buttonClass={"button-black"}onClick={handleCancel} />
        </div>
      )
  )}
          </div>
        </div>
    </section>
  );
}

export default BookingForm;
