import React from 'react';
import Button from './button';

function Hero() {
  return (
    <article style={{backgroundColor: "red", padding: "20px", maxHeight: "200px"}} className={"specials-holder"}>
      <div> 
        {/* left side */}
        <p className={"hero-title"}>Little Lemon</p>
        <p className={"hero-subtitle"}>Chicago</p>
        <p>We are a family owned Mediterranean restaurant, focused on traditional recipes served with a modern twist.</p>
        <Button buttonText={"Reserve a Table"} buttonClass={"button-yellow"} />
      </div>
      <img style={{ maxHeight: "200px" }} className="image-rounded"  src="Mario and Adrian A.jpg" />
       
    </article>

  );
}

export default Hero;
