import React from 'react';
import Button from './button';


function Hero() {
  return (
    <section style={{backgroundColor: "#495E57"}} >
      <div style={{maxWidth: "400px", padding: "20px"}}>
        {/* left side */}
        <p className={"hero-title"}>Little Lemon</p>
        <p className={"hero-subtitle"}>Chicago</p>
        <p style={{color: "white"}}>We are a family owned Mediterranean restaurant, focused on traditional recipes served with a modern twist.</p>
        <Button buttonText={"Reserve a Table"} buttonClass={"button-yellow"} />
      </div>

      <img style={{ maxHeight: "200px" }} className="image-rounded"  src="Mario and Adrian A.jpg" />

    </section>

  );
}

export default Hero;
