import React, { useState } from 'react';
import Nav from './nav';
import LogoImg from './logo_img';
import useWindowSize from '../utilities/useWindowSize';


function Header() {
  const { width } = useWindowSize();
  const isMobile = width < 800;

    // useState hook to manage the showMenu boolean. needs state to trigger rerenders
    const [showMenu, setShowMenu] = useState(false);

     // Function to toggle showMenu
  const toggleMenu = () => {
    setShowMenu(prevState => !prevState);
  };


  return (
    <header className='section-container'>
<div className='width-limiter'>
      {
        isMobile ? (
          // Mobile version
          <div style={{display: "flex", justifyContent: "center"}}>
            <h2 style={{position: "absolute", left: "20px"}} onClick={toggleMenu}>{showMenu ? "X" : "open!"}</h2>
              {/* move the above pseudo code into a function that flips the showMenu bool */}
            <div style={{display: showMenu ? "flex" : "none"}}>


              <Nav isVertical={true}/>
            </div>
            <LogoImg />
          </div>
        ) :
        (
          // Desktop version
          <div className="width-limiter, header-content">
            <LogoImg />
            <Nav />
          </div>
        )
      }
      </div>
    </header>
  );
}

export default Header;
