import React from 'react';
import Nav from './nav';
import LogoImg from './logo_img';
import useWindowSize from '../utilities/useWindowSize';

function Header() {
  const { width } = useWindowSize();
  const isMobile = width < 800;
  let showMenu = false;

  return (
    <header style={{paddingLeft: "25px", paddingRight: "25px"}}>
      {
        isMobile ? (
          // Mobile version
          <div style={{display: showMenu ? "flex" : "none", justifyContent: "center"}}>
            <Nav isVertical={true}/>
            <LogoImg />
          </div>
        ) : 
        (
          // Desktop version
          <div className="header-content">
            <LogoImg />
            <Nav />
          </div>
        )
      }
    </header>
  );
}

export default Header;
