import React from 'react';
import Nav from './nav';
import LogoImg from './logo_img';
import useWindowSize from '../utilities/useWindowSize';

function Header() {
  const { width } = useWindowSize();
  const isMobile = width < 800;
    // useState hook to manage the showMenu boolean
    const [showMenu, setShowMenu] = useState(false);

  return (
    <header style={{paddingLeft: "25px", paddingRight: "25px"}}>
      {
        isMobile ? (
          // Mobile version
          <div style={{display: "flex", justifyContent: "center"}}>
            <p onClick={showMenu = !showMenu}>{showMenu ? "X" : "Henlo"}</p>
              {/* move the above pseudo code into a function that flips the showMenu bool */}
            <div style={{display: showMenu ? "flex" : "none"}}> 


              <Nav isVertical={true}/>
            </div>
            <LogoImg />
          </div>
        ) : 
        (
          // Desktop version
          <div className="header-content">
            <LogoImg />
            <Nav />
          </div>
        )
      }
    </header>
  );
}

export default Header;
