import React, { useState } from 'react';
import Button from './button';


function BookingForm() {

    // State variables to manage form data
    const [date, setDate] = useState('');
    const [time, setTime] = useState('');
    const [guests, setGuests] = useState(1);
    const [occasion, setOccasion] = useState('');

    const [isConfirming, setIsConfirming] = useState(false);

    // Handle form submission
    const handleSubmit = (e) => {
      e.preventDefault();
      console.log("Form Submitted:", { date, time, guests, occasion });
      // You can add logic here to send form data to your server or API
    };

  return (
    <section className={"section-container"} style={{color: "white", backgroundColor: "#495E57"}}>
        <div className={"width-limiter" }>

        <form onSubmit={handleSubmit} className="reservation-form">
      <label htmlFor="date">Date:</label>
      <input
        type="date"
        id="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        required
      />

      <label htmlFor="time">Time:</label>
      <input
        type="time"
        id="time"
        value={time}
        onChange={(e) => setTime(e.target.value)}
        required
      />

      <label htmlFor="guests">Number of Guests:</label>
      <input
        type="number"
        id="guests"
        min="1"
        max="20"
        value={guests}
        onChange={(e) => setGuests(e.target.value)}
        required
      />

      <label htmlFor="occasion">Occasion:</label>
      <select
        id="occasion"
        value={occasion}
        onChange={(e) => setOccasion(e.target.value)}
        required
      >
        <option value="" disabled>Select an occasion</option>
        <option value="birthday">Birthday</option>
        <option value="anniversary">Anniversary</option>
      </select>

      <Button type="submit" Buttontext="Submit Reservation" link="/confirmreservation" />
    </form>
        </div>
    </section>
  );
}

export default BookingForm;
