import React from 'react';
import SpecialSingle from './specialsingle';
import Button from './button';

function SpecialsHolder() {
  return (
    <section className={"specials-holder"}>
      <div className={"spacer-spread"}>
      <h1>Specials</h1>
      <Button buttonText={"Online Menu"} buttonClass={"black-button"}/>
      </div>
      
      <SpecialSingle 
      title="Greek Salad" 
      description="The famous Greek salad of crispy lettuce, peppers, olives and our Chicago style feta cheese, garnished with crunchy garlic and rosemary croutons." 
      image="greek-salad.jpg" 
      price="12.99" />

      <SpecialSingle 
      title="Bruchetta" 
      description="Our Bruschetta is made from grilled bread that has been smeared with garlic and seasoned with salt and olive oil." 
      image="bruchetta.jpg" 
      price="5.99" />

      <SpecialSingle 
      title="Lemon Dessert" 
      description="This comes straight from grandmaâ€™s recipe book, every last ingredient has been sourced and is as authentic as can be imagined." 
      image="lemon-dessert.jpg" price="5.00" />
    </section>
  );
}

export default SpecialsHolder;
