import React from 'react';

function TestimonialItem({ rating, image, name, reviewText }) {
  return (
    <div style={{paddingLeft: "15px"}}>
      {/* src reads directly from the public folder, image will always be jpg, just need to give its name */}
      <div style={{ display: "flex", alignItems: "center" }}>
        <img style={{height: "100px", width: "100px", objectFit: "cover"}} src={`/${image}`} alt={name} />
        <div> 
          <h3 style={{paddingLeft: "10px"}}>{name}{rating}</h3>
          <h3 style={{paddingLeft: "10px"}}></h3>
        </div>
      </div>
        <div style={{ display: "flex", alignItems: "center" }}>
          <p>{reviewText}</p>

        </div>
    </div>
  );
}

export default TestimonialItem;
