import React from 'react';
import Button from './button';

function Hero() {
  return (
    // this background should go across the entire screen.
    <section className={"section-container"} style={{backgroundColor: "#495E57"}}>
      {/* dizxe  */}
      <div className={"width-limiter" }>

          {/* i went as far as just setting the following div manually like so and nothing seems to work without spacing issues,
          this will need to be redone
        <div style={{width: "970px", marginRight: "30px"}}> */}

          <div id={"hero-inner-container"}>

            <div>
              {/* left side */}
              <h1 className={"hero-title"}>Little Lemon</h1>
              <h2 className={"hero-subtitle"}>Chicago</h2>
              <p style={{color: "white"}}>We are a family owned Mediterranean restaurant, focused on traditional recipes served with a modern twist.</p>
              <Button buttonText={"Reserve a Table"} buttonClass={"button-yellow"} />
            </div>

          <img id={"hero-img"} style={{ maxHeight: "200px" }} className="image-rounded" src="Mario and Adrian A.jpg" />
          </div>
      </div>

    </section>

  );
}

export default Hero;
