import React, { createContext, useContext, useState, useEffect } from 'react';

// Create context
const BookingTimesContext = createContext({
  // bookingTimes: [],
  // setTimeUnavailable: () => {}
});

// initial all booking times (that would come from API)
const initialBookingTimes = [
  { time: "11:00", isAvailable: true },
  { time: "11:30", isAvailable: true },
  { time: "12:00", isAvailable: true },
  { time: "12:30", isAvailable: true },
  { time: "13:00", isAvailable: true },
  { time: "13:30", isAvailable: true },
  { time: "14:00", isAvailable: true },
  { time: "14:30", isAvailable: true },
  { time: "15:00", isAvailable: true },
  { time: "15:30", isAvailable: true },
  { time: "16:00", isAvailable: true },
  { time: "16:30", isAvailable: true },
  { time: "17:00", isAvailable: true },
  { time: "17:30", isAvailable: true },
  { time: "18:00", isAvailable: true },
  { time: "18:30", isAvailable: true },
  { time: "19:00", isAvailable: true },
  { time: "19:30", isAvailable: true },
  { time: "20:00", isAvailable: true },
  { time: "20:30", isAvailable: true }
];

// Provider component
export const BookingTimesProvider = ({ children }) => {
  const [bookingTimes, setBookingTimes] = useState([]);

  // useEffect(() => {
  //   const fetchBookingTimes = async () => {
  //     try {
  //       const date = new Date(); // Gets today's date
  //       const apiResponse = await fetchAPI(date); // Calls external fetchAPI function
  //       const updatedBookingTimes = apiResponse.map((time) => ({
  //         time,
  //         isAvailable: true /* Defaults to all times being available so code works. API works so that whatever times are
  //         found are assumed as the available ones */
  //       }));

  //       setBookingTimes(updatedBookingTimes); // Update state with fetched data
  //     } catch (error) {
  //       console.error("Error fetching booking times:", error);
  //     }
  //   };

  //   fetchBookingTimes();
  // }, []); // Dependency array ensures it runs only once on mount

  // Function to set a specific time as unavailable
  const setTimeUnavailable = (selectedTime) => {
    setBookingTimes((prevBookingTimes) =>
      prevBookingTimes.map((timeSlot) =>
        timeSlot.time === selectedTime
          ? { ...timeSlot, isAvailable: false }
          : timeSlot
      )
    );
  };

  return (
    <BookingTimesContext.Provider value={{ bookingTimes, setTimeUnavailable }}>
      {children}
    </BookingTimesContext.Provider>
  );
};

// Custom hook to use the context
export const useBookingTimes = () => {
  return useContext(BookingTimesContext);
};

export default BookingTimesContext;
