import React from 'react';
import Button from './button';


function Hero() {
  return (
    // this background should go across the entire screen.
    <section className={"grid-container, vertical-padding, screen-wide-banner"} style={{backgroundColor: "#495E57"}}>

      <div style={{width: "970px", marginRight: "30px"}}>

        {/* i went as far as just setting things manually like so and nothing seems to work:
              <div style={{width: "970px", marginRight: "30px"}}> */}

          <div className={"inner-padding"} id={"hero-inner-container"}>

            <div style={{paddingBottom: "20px"}}>
              {/* left side */}
              <p className={"hero-title"}>Little Lemon</p>
              <p className={"hero-subtitle"}>Chicago</p>
              <p style={{color: "white"}}>We are a family owned Mediterranean restaurant, focused on traditional recipes served with a modern twist.</p>
              <Button buttonText={"Reserve a Table"} buttonClass={"button-yellow"} />
            </div>

          <img style={{ maxHeight: "200px" }} className="image-rounded"  src="Mario and Adrian A.jpg" />
          </div>
      </div>

    </section>

  );
}

export default Hero;
