import React, { useState } from 'react';
import { useBooking } from './bookingContext';
import Button from './Button';

function BookingForm() {
  const {
    firstName: defaultFirstName,
    lastName: defaultLastName,
    date: defaultDate,
    time: defaultTime,
    guests: defaultGuests,
    seating: defaultSeating,
    occasion: defaultOccasion,
    setFirstName,
    setLastName,
    setDate,
    setTime,
    setGuests,
    setSeating,
    setOccasion,
    setStep
  } = useBooking();

  // Local state initialized with context values
  const [firstName, setLocalFirstName] = useState(defaultFirstName);
  const [lastName, setLocalLastName] = useState(defaultLastName);
  const [date, setLocalDate] = useState(defaultDate);
  const [time, setLocalTime] = useState(defaultTime);
  const [guests, setLocalGuests] = useState(defaultGuests);
  const [seating, setLocalSeating] = useState(defaultSeating);
  const [occasion, setLocalOccasion] = useState(defaultOccasion);

  const handleFormSubmit = (e) => {
    e.preventDefault();

    // Update context with local state values on submit
    setFirstName(firstName);
    setLastName(lastName);
    setDate(date);
    setTime(time);
    setGuests(guests);
    setSeating(seating);
    setOccasion(occasion);

    setStep("confirm"); // Move to confirmation step
  };

  return (
    <form onSubmit={handleFormSubmit} className="reservation-form">
      <div className='form-row-single'>
        <label htmlFor="first-name">First Name</label>
        <input
          type="text"
          id="first-name"
          value={firstName}
          onChange={(e) => setLocalFirstName(e.target.value)}
          placeholder="First Name"
          required
        />
      </div>
      <div className='form-row-single'>
        <label htmlFor="last-name">Last Name</label>
        <input
          type="text"
          id="last-name"
          value={lastName}
          onChange={(e) => setLocalLastName(e.target.value)}
          placeholder="Last Name"
          required
        />
      </div>
      <div className='form-row-single'>
        <label htmlFor="date">Date</label>
        <input
          type="date"
          id="date"
          value={date}
          onChange={(e) => setLocalDate(e.target.value)}
          required
        />
      </div>
      {/* Repeat for other fields */}
      <div className='form-row'>
        <Button type="submit" buttonClass="button-yellow" buttonText="Let's Do it!" />
      </div>
    </form>
  );
}

export default BookingForm;
